import { GET_PLAYERS, SET_STAGE, START_GAME, CHANGE_PLAYER_VIEW} from './types'

export const getPlayersInRoom = (room_id) => async dispatch => {
    const url = `http://localhost:3000/players-in-room/${room_id}`
    var res = await fetch(url)
    var players = await res.json();
    var playerNames = [];
    players.map((player) => (
        playerNames.push(player.name)
    ))
    dispatch({
        type: GET_PLAYERS,
        playerNames,
        players
    })
}

export const setStage = () => dispatch => {
    dispatch({
        type: SET_STAGE
    })
}

export const startingGame = () => dispatch =>  {
    dispatch({
        type: START_GAME
    })
}

export const changePlayerView = () => dispatch => {
    dispatch({
        type: CHANGE_PLAYER_VIEW
    })
}

export const generateQuestionsForRound = (room) => async dispatch => {

    const url = 'http://localhost:3000/collectioncards'
    var res = await fetch(url)
    var questions = await res.json();

    const url2 = 'http://localhost:3000/players-in-game/'
    var res2 = await fetch(url2)
    var allPlayers = await res2.json()

    var playersInRoom = allPlayers.filter((player) => player.room_id === room)


    var count = 0;

    playersInRoom.forEach((player) => {
        if(player.questions_round_1 === null && player.questions_round_2 === null && player.questions_round_3 === null) {
            player.questions_round_1 = [];
            player.questions_round_2 = [];
            player.questions_round_3 = [];
        }
        if(player.answers_round_1 === null && player.answers_round_2 === null && player.answers_round_3 === null) {
            player.answers_round_1 = [];
            player.answers_round_2 = [];
            player.answers_round_3 = [];
        }
    })

    while(count !== ((playersInRoom.length * 2))) {
        var user1 = playersInRoom[Math.floor(Math.random() * playersInRoom.length)]
        var user2 = playersInRoom[Math.floor(Math.random() * playersInRoom.length)]
        if(user1.name === user2.name || user1.questions.length === 2 || user2.questions.length === 2  || ((user1.questions[user1.questions.length-1] === user2.questions[user2.questions.length-1]) && user1.questions[0] !== undefined)) {
            continue
        }
        user1.questions.push(questions[Math.floor(Math.random() * questions.length)])

        var addedQuestion = user1.questions[user1.questions.length-1]
        var index = questions.indexOf(addedQuestion)
        questions.splice(index, 1)
        user2.questions.push(user1.questions[user1.questions.length-1])
        debugger
        // eslint-disable-next-line no-loop-func
        playersInRoom.forEach((player) => {
            if(user1.name === player.name) {
                player.questions = user1.questions
            }
            if(user2.name === player.name) {
                player.questions = user2.questions
            }
        })
        count++;
    }

    console.log("if I have three users, there should be six questions in total: Everyone should have four prompts", playersInRoom)
}
