import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import queryString from 'query-string';
import io from 'socket.io-client';
import {setName, setRoom} from '../../actions/joinRoomActions'
import {getStandardDeck, setMessages} from '../../actions/gameActions'


let socket;


export const MainLobby = (props) => {
    const dispatch = useDispatch();
    const roomId = useSelector(state => state.joinRoom.room_id)
    const username = useSelector(state => state.joinRoom.name)
    const deck = useSelector(state => state.game.prompts)
    const deckName = useSelector(state => state.game.deckName)
    const allMessages = useSelector(state => state.game.messages)
    const message = useSelector(state => state.game.message)
    const ENDPOINT = 'localhost:3000'
    const test = useSelector(state => state.game.test)


    useEffect(() => {
        const {name, room} = queryString.parse(props.location.search)
        socket = io(ENDPOINT);
        dispatch(setName(name))
        dispatch(setRoom(room))
        dispatch(getStandardDeck())

        socket.emit('join',  {name, room}, () => {

        });

        return() => {
            socket.emit('disconnect');

            socket.off();
        }
    }, [ENDPOINT])

    useEffect(() => {
        socket.on('message', (message) => {
            console.log(message)
            setMessages(message);
        })
    }, [allMessages])

    const sendMessage = (event) => {
        event.preventDefault();
        if(message) {
            socket.emit('sendMessage', message, () => setMessages(''));
        }
    }
    console.log(message, allMessages)
    console.log('Testing Action.payload', test)
    return (
        <div>
            <h1>Main Lobby</h1>
            <input 
            value={message} 
            onChange={(e) => setMessages(e.target.value)}
            onKeyPress={event => event.key === 'Enter' ? sendMessage(event) : null}/>
        </div>
    )
}

export default MainLobby
