import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import queryString from 'query-string';
import io from 'socket.io-client';
import {setRoom} from '../../actions/joinRoomActions'
import {getQuestions, getPlayers, removePlayer} from '../../actions/gameActions'


let socket;


export const MainLobby = (props) => {
    const dispatch = useDispatch();
    const roomID = useSelector(state => state.userHome.room_id)
    const questions = useSelector(state => state.game.questions)
    const ENDPOINT = 'localhost:3000'
    const goToLobby = useSelector(state => state.userHome.goToLobby)

    useEffect(() => {
        const {name, room} = queryString.parse(props.location.search)
        socket = io(ENDPOINT);

        if(goToLobby === true) {
            dispatch(setRoom(roomID))
            dispatch(getQuestions())
        } 
        socket.emit('join',  {name, room}, () => {
            dispatch(getPlayers(name))
        }); 

        return() => {
            socket.emit('disconnect');
            dispatch(removePlayer())
            socket.off();
        }
    }, [ENDPOINT])

   useEffect(() => {
       socket.emit('message')
   })
    return (
        <div>
            <h1>Main Lobby</h1>
            <div>
                <p> Users currently in the lobby </p>

                <p>A button to start the game that only the main user can see</p>

                <p> where the questions will be loaded</p>
            </div>
        </div>
    )
}

export default MainLobby
