import {ADD_PLAYER, GET_PLAYERS, SET_STAGE, START_GAME } from "../actions/types";
import storage from 'redux-persist/lib/storage';
import { persistReducer } from 'redux-persist';

const initialState = {
    playerNames: [],
    playersInRoom: [],
    gameStarted: false,
    stage: 1
}

function gameReducer (state = initialState, action) {
    switch(action.type) {
        case GET_PLAYERS:
          return {
            ...state,
            playerNames: action.playerNames,
            playersInRoom: action.players
          }
        case SET_STAGE:
          return {
            ...state,
            stage: state.stage + 1
          }
        case START_GAME:
          return {
            ...state,
            gameStarted: true
          }
        default:
          return {
            ...state
          }
    }
}

export default gameReducer
