import React, { useState, useEffect } from 'react'
import RoundWinner from './RoundWinner'
import { useDispatch, useSelector } from 'react-redux';
import io from 'socket.io-client';
import './Game.css'
import { setStage, updateAnswersForVoting, addPoints, determineWinner } from '../../actions/gameActions'
import { setIsActive, setSeconds } from '../../actions/timerActions'
import Timer from '../Timer/Timer'

const ENDPOINT = 'localhost:3000'
let socket = io(ENDPOINT);

let callback;

export const Game = (props) => {
    const dispatch = useDispatch();
    const stage = useSelector(state => state.game.stage);
    const isActive = useSelector(state => state.timer.isActive)
    const seconds = useSelector(state => state.timer.seconds)
    const playersInRoom = useSelector(state => state.game.playersInRoom)
    const count_of_answers = useSelector(state => state.player.count_of_answers)
    const questions_round_1 = useSelector(state => state.game.questions_round_1)
    const selectedQuestion = useSelector(state => state.game.selectedQuestion)
    const round_winnner = useSelector(state => state.game.round_winnner)


    useEffect(() => {
        const timer = setTimeout(() => {
            dispatch(setStage())
            dispatch(setIsActive())
            dispatch(setSeconds(80))
            // give all the players their prompts to answer on screen
        }, 5000);
        socket.on('ready-to-vote', ({ allPlayersInGame, selectedQuestion }) => {
            dispatch(setIsActive())
            dispatch(updateAnswersForVoting(allPlayersInGame, selectedQuestion))
            dispatch(setStage())
        })
        return () => clearTimeout(timer);
    }, []);

    useEffect(() => {
        socket.on('cast-vote-round-1-to-host', (answer, callback) => {
            dispatch(addPoints(answer))
        })
    }, [])

    if(count_of_answers === 3) {

    }

    useEffect(() => {
        if(count_of_answers === 1) {
            console.log('run this')
        }
        if(count_of_answers === 2) {
            console.log('run this maybe')
        }
        if(count_of_answers === 3) {
            console.log('when does this run? Does this or update?')
            dispatch(determineWinner())
        }
    }, [count_of_answers])

    if (stage === 1) {
        return (
            <div>
                <p>Here are the Rules</p>
                <p> Each player will be given a prompt to answer. When everyone is done answering, </p>
                <p> You will vote on your favorite answers!</p>
            </div>
        )
    }
    if (stage === 2) {
        return (
            <div>
                <Timer />
            </div>
        )
    }
    if (stage === 3) {
        return (
            <div>
                <div>
                    {questions_round_1[selectedQuestion].prompt}
                </div>
                {questions_round_1[selectedQuestion].answers.map(answer => (
                    <div>
                        <h1>{answer}</h1>
                    </div>
                ))}
                <h1>{round_winnner}</h1>
            </div>
        )
    }
    return (
        <div className='game-container'>
            <div><p>Timer: </p></div>

            <div><p>Question:</p></div>
            <h1>The Game</h1>
        </div>
    )
}

export default Game
