import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import queryString from 'query-string';
import Game from '../Game/Game';
import PlayerContainer from './PlayerContainer'
import io from 'socket.io-client';
import { Button } from 'semantic-ui-react';
import {setRoom} from '../../actions/joinRoomActions'
import {addPlayer, removeAllPlayers, startGame, getPlayers} from '../../actions/gameActions'


let socket;
const ENDPOINT = 'localhost:3000'

export const MainLobby = (props) => {
    const dispatch = useDispatch();
    const roomID = useSelector(state => state.userHome.room_id)
    const gameStarted = useSelector(state => state.game.gameStarted)
    const playerList = useSelector(state => state.game.players)
    const playersInGame = useSelector(state => state.game.playersInGame)

    useEffect(() => {
            const {name, room} = queryString.parse(props.location.search)
            socket = io(ENDPOINT);
            socket.emit('join',  {name, room}, () => {});
            return() => {
                socket.emit('disconnect');
                socket.off();
            }
    }, [props.location.search]);

    useEffect(() => {
        const player = queryString.parse(props.location.search)
        if(!playerList.includes(player.name)) {
            dispatch(addPlayer(player))
            dispatch(setRoom(roomID))
            socket.emit('players-in-loading-circle', roomID)
        }
    }, [dispatch, playerList, props.location.search, roomID]);

    useEffect(() => {
        socket.on('players-in-loading-circle', (player) => {
            dispatch(getPlayers(player.room))
        })
    })



    if(gameStarted === true) {
        return (<Game/>)
    } else {
    return (
        <div>
            <h1>Main Lobby</h1>
            <div className='main-lobby-screen'>
                <button onClick={()=> dispatch(removeAllPlayers())}>Clear Lobby</button>
                <p> Users currently in the lobby Max 8</p>
                <PlayerContainer players={playerList}/>
                <Button onClick={() => dispatch(startGame())}>Start Game</Button>
            </div>
        </div>
    )}
}

export default MainLobby
