import {SET_PROMPTS, GET_PLAYER_INFO} from './types'



export const setPrompts = (player) => dispatch => {
    dispatch({
        type: SET_PROMPTS,
        action: player
    })
}

export const getPlayerInfo = (playerName, room) => async dispatch => {
    const url = 'http://localhost:3000/players-in-game'
    const res = await fetch(url)
    const players = await res.json()

    var prompts = [];

    players.forEach((player) => {
        if(player.name === playerName && player.room_id === room) {
            prompts.push(player.questions_round_1[0], player.questions_round_1[1], player.questions_round_2[0], player.questions_round_2[1], player.questions_round_3[0], player.questions_round_3[1])
        }
    })
    debugger
    dispatch({
        type: GET_PLAYER_INFO,
        prompts
    })
}
