import {FETCH_COLLECTION, ADD_PLAYER, REMOVE_PLAYER, REMOVE_ALL_PLAYERS, START_GAME} from './types'



export const getQuestions = () => dispatch => {
    fetch('http://localhost:3000/collectioncards')
    .then(res => res.json())
    .then((collection) => {
        var title = collection[0].name
        var questions = [];

        collection.map((index) => (
            questions.push(index.prompt)
        ))

        dispatch({
            type: FETCH_COLLECTION,
            title,
            questions
        })
    } 
    )
}

export const startGame = () => dispatch =>  {
    dispatch({
        type: START_GAME
    })
}

export const addPlayer = (name, room) => dispatch => {
    fetch('http://localhost:3000/playersInGame', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json'
    },
    body: JSON.stringify(name, room)
    })
    .then(res => res.json())
    .then(newplayer => dispatch({
        type: ADD_PLAYER,
        payload: newplayer
    }))
}


export const removePlayer = (name) => dispatch => {
    dispatch({
        type: REMOVE_PLAYER,
        payload: name
    })
}

export const removeAllPlayers = () => dispatch => {
    dispatch({
        type: REMOVE_ALL_PLAYERS
    })
}
