import React, {useEffect} from 'react'
import { Button, Form} from 'semantic-ui-react'
import { connect } from 'react-redux'
import { fetchUsers, createUser } from '../../actions/userActions'


export function SignupForm(props) {

    useEffect(() => {
        console.log("This is like componentDidMount")
        props.fetchUsers()
    }, [])
    async function handleSubmit(e) {
        e.preventDefault()
        const new_user = {
            username: e.target.name.value,
            email: e.target.email.value,
            password: e.target.password.value
        }
        props.createUser(new_user)
    }
//  if (this.state.redirect) {
// return <Redirect push to={`/student/${this.state.userID}/home`}></Redirect>
    return(
        <div>
        <h1>This is the Signup Form</h1>
        <p>{console.log(props.users)}</p>
        <Form onSubmit={handleSubmit}>
        <Form.Field required>
            <label>Name: </label>
            <input placeholder='Name' name='name'/>
        </Form.Field>
        <Form.Field required>
            <label>Email: </label>
            <input placeholder='email' name='email'/>
        </Form.Field>
        <Form.Field required>
            <label>Password</label>
            <input type='password' name='password'/>
        </Form.Field>
        <Form.Field required>
            <label>Confirm Password</label>
            <input type='password' name='password_confirmation'/>
        </Form.Field>
        <Button type='submit' className='signupButton'>Submit</Button>
        </Form>
        </div>
    )
}


const mapStateToProps = state => ({
    users: state.signup.users
})

export default connect(mapStateToProps, { fetchUsers, createUser })(SignupForm)
