import {SIGN_UP_SUCCESS, FETCH_USERS} from './types'

export const fetchUsers = () => dispatch => {
       fetch('http://localhost:3000/users')
       .then(res => res.json())
       .then(users => dispatch({
           type: FETCH_USERS,
           allUsers: users
       }))
}
