import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import io from 'socket.io-client';
import './Game.css'
import './votingScreenRound1.css'
import '../Timer/loadingCircle.css'
import { setStage, updateAnswersForVoting, addPoints, determineWinner } from '../../actions/gameActions'
import { setIsActive, setSeconds } from '../../actions/timerActions'
import Timer from '../Timer/Timer'
const ENDPOINT = 'localhost:3000'
let socket = io(ENDPOINT);


export const Game = (props) => {

    const dispatch = useDispatch();
    const stage = useSelector(state => state.game.stage);
    const isActive = useSelector(state => state.timer.isActive)
    const seconds = useSelector(state => state.timer.seconds)
    const playersInRoom = useSelector(state => state.game.playersInRoom)
    const count_of_answers = useSelector(state => state.player.count_of_answers)
    const questions_round_1 = useSelector(state => state.game.questions_round_1)
    const selectedQuestion = useSelector(state => state.game.selectedQuestion)
    const round_winnner = useSelector(state => state.game.round_winnner)



    useEffect(() => {
        const timer = setTimeout(() => {
            dispatch(setStage())
            dispatch(setIsActive())
            dispatch(setSeconds(80))
            // give all the players their prompts to answer on screen
        }, 20000);
        socket.on('ready-to-vote', ({ allPlayersInGame, selectedQuestion }) => {
            dispatch(setIsActive())
            dispatch(updateAnswersForVoting(allPlayersInGame, selectedQuestion))
            dispatch(setStage())
        })
        return () => clearTimeout(timer);
    }, []);

    useEffect(() => {
        socket.on('cast-vote-round-1-to-host', (answer, callback) => {
            dispatch(addPoints(answer))
        })

    }, [count_of_answers])

    // useEffect(() => {
    //     if (count_of_answers === playersInRoom.length) {
    //         dispatch(determineWinner())
    //     }
    // }, [count_of_answers])

    useEffect(() => {
        if(count_of_answers === 3) {
         var num_of_answers = count_of_answers 
         dispatch(determineWinner(num_of_answers))
     }
    }, [count_of_answers])


    console.log(round_winnner)

    if (stage === 1) {
        return (
            <div className='main-container'>
            <div class="sp-container">
                <div class="sp-content">
                    <h2 class="frame-1">Each player will be given random prompts to answer each round</h2>
                    <h2 class="frame-2">After each round of answers, you will vote on the best answer for each prompt!</h2>
                    <h2 class="frame-3">You will have 60 seconds for each round! </h2>
                    <h2 class="frame-4">Good Luck!</h2>
                </div>
            </div>
        </div>
        )
    }
    if (stage === 2) {
        return (
        <div className='timer-container'>
                <Timer />
            <div className='loading-container'>
                <div class="loader">
                    <span></span>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
                </div>
        </div>
        )
    }
    if (stage === 3) {
        return (
        <div className='votingScreenRound1'>
          <div>
            <h1>{questions_round_1[selectedQuestion].prompt}</h1> 
          </div>
            <h2 className='answer1Round1'>Answer 1</h2>
            <h2 className='answer2Round1'>Answer 2</h2>
            {questions_round_1[selectedQuestion].answers.map((answer,i) => (
                <div>
                    <p className={`speech${i}`}>{answer}</p>
                </div>
            ))}
            {round_winnner}
            <img className='redRound1Voting' src='https://jackboxgames.com/wp-content/uploads/2019/07/Red.gif' alt='quiplash icon'></img>
            <img className='blueRound1Voting' src='https://jackboxgames.com/wp-content/uploads/2019/07/Blue.gif' alt='quiplash icon'></img>
        </div>
        )
    }
    return (
        <div className='game-container'>
        </div>
    )
}

export default Game
