import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import queryString from 'query-string';
import io from 'socket.io-client';
import {addPlayer, getPlayersInRoom, setStage} from '../../actions/gameActions'

let socket;
const ENDPOINT = 'localhost:3000'

export const MainLobby = (props) => {
    const dispatch = useDispatch();
    const playerNames = useSelector(state => state.game.playerNames)
    const playersInRoom = useSelector(state => state.game.playersInRoom)
    const stage = useSelector(state => state.game.stage)

    useEffect(() => {
        const {name, room} = queryString.parse(props.location.search)
        socket = io(ENDPOINT);
        socket.emit('join', {name, room}, () => {
            const player = {name: name, room: room}
            dispatch(addPlayer(player))
        })
    }, [dispatch, props.location.search])

    useEffect(() => {
        socket.on('get-players-in-game', (room) => {
            dispatch(getPlayersInRoom(room))
        })
    })

    // useEffect(() => {
    //     socket.on('get-players', (room) => {
    //         console.log('run ')
    //         dispatch(getPlayersInRoom(room))
    //     })
    // }, [dispatch, playerNames, stage])
    return (
        <div>
            <div className='main-lobby-screen'>
                <p> Users currently in the lobby Max 8</p>
            </div>
        </div>
    )
    }
export default MainLobby
