import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import queryString from 'query-string';
import PlayerList from './PlayerList'
import io from 'socket.io-client';
import { Button } from 'semantic-ui-react'
import {setRoom} from '../../actions/joinRoomActions'
import {getQuestions, addPlayer, removePlayer, removeAllPlayers} from '../../actions/gameActions'
import $ from 'jquery';
let socket;


export const MainLobby = (props) => {
    const dispatch = useDispatch();
    const roomID = useSelector(state => state.userHome.room_id)
    const ENDPOINT = 'localhost:3000'
    const createdLobby = useSelector(state => state.userHome.goToLobby)
    var questions = useSelector(state => state.game.questions)
    var players = useSelector((state => state.game.players))
    const gameStarted = useSelector(state => state.game.gameStarted)

    useEffect(() => {
        const {name, room} = queryString.parse(props.location.search)
        socket = io(ENDPOINT);

        if(createdLobby === true) {
            dispatch(setRoom(roomID))
            dispatch(getQuestions())
        } 
        socket.emit('join',  {name, room}, () => {
        });
        return() => {
            socket.emit('disconnect');
            socket.off();
        }
    }, [ENDPOINT])


    useEffect(() => {
        socket.on('addPlayer', (player) => {
            dispatch(addPlayer(player))
        })
    })

    const startGame = () => {

    }

    return (
        <div>
            <h1>Main Lobby</h1>
            <div className='main-lobby-screen'>
                <button onClick={()=> dispatch(removeAllPlayers())}>Clear Lobby</button>
                <p> Users currently in the lobby Max 8</p>
                <ul className='circle-container'>
                    {players !== undefined ? players.map((player) => {
                        return <li className='playerInfo' ><img src='https://sanin.dev/assets/thumbnails/quiplash.png'></img><p>Name: {player}</p></li>
                    }) : <p> None</p>}
                </ul>
                <Button onClick={() => startGame()}>Start Game</Button>
            </div>
            <div className='game-screen'></div>
        </div>
    )
}

export default MainLobby
