import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import io from 'socket.io-client';
import './Game.css'
import {setStage} from '../../actions/gameActions'

let socket;
const ENDPOINT = 'localhost:3000'



export const Game = (props) => {
    const dispatch = useDispatch();
    const stage = useSelector(state => state.game.stage);

    useEffect(() => {
        const timer = setTimeout(() => {
          dispatch(setStage())
          // give all the players their prompts to answer on screen
        }, 15000);
        return () => clearTimeout(timer);
      }, []);


    if(stage === 1) {
        return (
        <div>
            <p>Here are the Rules</p>
            <p> Each player will be given a prompt to answer. When everyone is done answering, </p>
            <p> You will vote on your favorite answers!</p>
        </div>
        )
    }
    if(stage === 2) {
        return (
            // users will have a time limit to answer questions that show up on their phone
            <div>
                <p> Timer: </p>
            </div>
        )
    }
    return (
        <div className='game-container'>
            <div><p>Timer: </p></div>

            <div><p>Question:</p></div>
            <h1>The Game</h1>
        </div>
    )
}

export default Game
