import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import queryString from 'query-string';
import PlayerList from './PlayerList'
import io from 'socket.io-client';
import {setRoom} from '../../actions/joinRoomActions'
import {getQuestions, addPlayer, removePlayer, removeAllPlayers} from '../../actions/gameActions'
import $ from 'jquery';
let socket;


export const MainLobby = (props) => {
    const dispatch = useDispatch();
    const roomID = useSelector(state => state.userHome.room_id)
    const ENDPOINT = 'localhost:3000'
    const createdLobby = useSelector(state => state.userHome.goToLobby)
    var questions = useSelector(state => state.game.questions)
    var playerArray = [];

    useEffect(() => {
        const {name, room} = queryString.parse(props.location.search)
        socket = io(ENDPOINT);

        if(createdLobby === true) {
            dispatch(setRoom(roomID))
            dispatch(getQuestions())
        } 
        socket.emit('join',  {name, room}, () => {
        });
        socket.emit('addPlayer', name)
        // dispatch(addPlayer(name))
        return() => {
            socket.emit('disconnect');
            socket.off();
        }
    }, [ENDPOINT])

    useEffect(() => {
        socket.on('allPlayers', function(name) {
            $('#circle-container').append($('<li>').text(name))
        })
    })
    return (
        <div>
            <h1>Main Lobby</h1>
            <div>
                <button onClick={()=> dispatch(removeAllPlayers())}>Clear All Players</button>
                <p> Users currently in the lobby Max 8</p>
                <ul className='circle-container'>

                </ul>
                <p>A button to start the game</p>
                <p> where the questions will be loaded</p>
            </div>
        </div>
    )
}

export default MainLobby
