import { FETCH_COLLECTION, GET_PLAYERS, REMOVE_PLAYER } from "../actions/types";
import storage from 'redux-persist/lib/storage';
import { persistReducer } from 'redux-persist';

const initialState = {
    title: '',
    questions: [],
    players: []
}

function gameReducer (state = initialState, action) {
    switch(action.type) {
        case FETCH_COLLECTION: 
          return {
            ...state,
            questions: action.questions,
            title: action.title

          }
        case GET_PLAYERS:
          if(players.includes(action.payload)) {
            return {...state}
          } else {
          return {
            ...state,
            players: [...state.players, action.payload]
          }
        }
        case REMOVE_PLAYER:

          return {
            ...state,
            players: state.players.filter((player) => (player !== action.payload))
          }
        default:
          return {
            ...state
          }
    }
}

const persistConfig = {
  key: 'gameReducer',
  storage: storage
}

export default persistReducer(persistConfig, gameReducer)
