import {FETCH_USERS, SIGN_UP, LOGGED_IN, LOGIN_ERROR, SET_USERNAME, SET_EMAIL, SET_PASSWORD, SET_C_PASSWORD} from './types'
export const fetchUsers = () => dispatch => {
       fetch('http://localhost:3000/users')
       .then(res => res.json())
       .then(users => dispatch({
           type: FETCH_USERS,
           payload: users
       }))
}

export const createUser = (userData) => dispatch => {
    fetch('http://localhost:3000/users', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({userData})
        })
        .then(res => res.json())
        .then(user => dispatch({
            type: SIGN_UP,
            payload: user
        }))
}

export const loginUser = (userData) => dispatch => {
    fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({userData})
        })
        .then(res => res.json())
        .then(user => {
            if(user.success === true) {
                dispatch({
                    type: LOGGED_IN,
                    payload: user
                })
            } else {
                dispatch({
                    type: LOGIN_ERROR
                })
            }
        })
}

export const setUsername = (username) => dispatch => {
    dispatch({
        type: SET_USERNAME,
        username
    })
}

export const setEmail = (email) => dispatch => {
    dispatch({
        type: SET_EMAIL,
        email
    })
}

export const setPassword = (password) => dispatch => {
    dispatch({
        type: SET_PASSWORD,
        password
    })
}

export const setConfPassword = (password_digest) => dispatch => {
    dispatch({
        type: SET_C_PASSWORD,
        password_digest
    })
}
