import {ADD_PLAYER, GET_PLAYERS, SET_STAGE } from "../actions/types";
import storage from 'redux-persist/lib/storage';
import { persistReducer } from 'redux-persist';

const initialState = {
    playerNames: [],
    playersInRoom: [],
    stage: 1
}

function gameReducer (state = initialState, action) {
    switch(action.type) {
        case GET_PLAYERS:
          return {
            ...state,
            playersInRoom: action.players
          }
        case ADD_PLAYER:
          if(state.playerNames.includes(action.payload)) {
            console.log('How many times does this run? Also...', action.players)
            return {
              ...state,
              playersInRoom: action.players
                   }
            } else {
                return {
                  ...state,
                  playerNames: [...state.playerNames, action.newPlayer],
                  playersInRoom: action.players
                }
              }
        // eslint-disable-next-line no-fallthrough
        case SET_STAGE:
          return {
            ...state,
            stage: state.stage + 1
          }
        default:
          return {
            ...state
          }
    }
}

export default gameReducer
