import {ADD_PLAYER, GET_PLAYERS } from "../actions/types";
import storage from 'redux-persist/lib/storage';
import { persistReducer } from 'redux-persist';

const initialState = {
    playerNames: [],
    playersInRoom: null
}

function gameReducer (state = initialState, action) {
    switch(action.type) {
      case ADD_PLAYER:
        return {
          ...state,
          playerNames: [...state.playerNames, action.newplayer.name]
        }
        case GET_PLAYERS:
          return {
            ...state,
            playersInRoom: action.players
          }
        default:
          return {
            ...state
          }
    }
}

const persistConfig = {
  key: 'gameReducer',
  storage: storage
}

export default persistReducer(persistConfig, gameReducer)
