import React from 'react'
import $ from 'jquery';
import './landing.css'
import overloads from '../../images/classOverlords.png'
import {Button, Form} from 'semantic-ui-react'

export const LandingPage = (props) => {

    const onLoginBtnClick = () => {
        if ($(".login").css("display") == "none") {
            $(".form").addClass("animated flipInY");
            $(".sign-up").hide();
            $(".login").show();
            setTimeout(function() {
                $(".form").removeClass("animated flipInY");
            }, 1000);
        } 
    }

    const onSignupBtnClick = () =>  {
        if ($(".sign-up").css("display") == "none") {
            $(".form").addClass("animated flipInY");
            $(".sign-up").show();
            $(".login").hide();
            setTimeout(function() {
                $(".form").removeClass("animated flipInY");
            }, 1000);
        }
    }


    return(
    <div>
        <div className="wrapper">
			<div className="grid-container">
              <div className='signupform'>
				<div className="form">
                    <div className='sign-up'>
					 <div className="signupGridContainer">

					    <div className="avatar-wrapper">	
					        <img src={overloads} className="avatar" alt='class overloards!!'/>
						</div>

                        <div className='signupUsername'>
                            <input type="username" placeholder="Username" required />
                        </div>
                        <div className='signupEmail'>
						    <input type="email" placeholder="Email" required autoComplete="new-password" />
                        </div>
                        <div className='signupPassword'>
						    <input type="password" placeholder="Password" required autoComplete="new-password"/>
                        </div>
                        <div className='signupConfPassword'> 
						    <input type="password" placeholder="Confirm Password" required autoComplete="new-password"/>
                        </div>
                        <div className='signupButtons'>
                            <div className="button-group">
						        <button className="btn login-btn" type="submit" onClick={() => onLoginBtnClick()}>LOGIN</button>
						        <button className="btn signup-btn" type="submit" onClick={() => onSignupBtnClick()}>SIGN UP</button>
					        </div>
                        </div>

					 </div>
                    </div>
                    <div className='login'>
					 <div className="loginGridContainer">
					    <div className="avatar-wrapper">	
					        <img src={overloads} className="avatar" alt='class overloards!!'/>
						</div>
                        <div>

                        </div>
                        <div className='loginEmail'>
                        <input type="email" placeholder="Email" required autoComplete="new-password" />
                        </div>
                        <div className='loginPassword'>
						<input type="password" placeholder="Password" required autoComplete="new-password"/>
                        </div>
					    <div className='loginButtons'>
                            <div className="button-group">
						        <button className="btn login-btn" type="submit" onClick={() => onLoginBtnClick()}>LOGIN</button>
						        <button className="btn signup-btn" type="submit" onClick={() => onSignupBtnClick()}>SIGN UP</button>
					        </div>
                        </div>
                     </div>
                     </div>
                    </div>
				</div>
			</div>
		</div>
	</div>
    )
}
