import {FETCH_COLLECTION, GET_PLAYERS, CLEAR_PLAYERS} from './types'



export const getStandardDeck = () => dispatch => {
    fetch('http://localhost:3000/collections/5')
    .then(res => res.json())
    .then((collection) => {
        var deckName = collection[0].name
        var prompts = [];

        collection.map((prompt) => {
            prompts.push(prompt)
        })

        dispatch({
            type: FETCH_COLLECTION,
            deckName,
            prompts
        })
    } 
    )
}

export const getPlayers = (player) => dispatch => {
    dispatch({
        type: GET_PLAYERS,
        payload: player

    })
}

export const clearPlayers = () => dispatch => {
    dispatch({
        type: CLEAR_PLAYERS
    })
}
