import {GO_TO_LOBBY, GENERATE_ROOM_ID} from '../actions/types'
import persistReducer from 'redux-persist/es/persistReducer';
import storage from 'redux-persist/lib/storage';


const initialState = {
    goToLobby: false,
    room_id: ''
}


function userHomepageReducer(state = initialState, action) {
    switch(action.type) {
        case GO_TO_LOBBY:
            return {
                ...state,
                goToLobby: true
            }
        case GENERATE_ROOM_ID:
            return {
                ...state,
                room_id: action.payload
            }
        default:
            return {
                ...state
            }
    }
}

const persistConfig = {
    key: 'landing',
    storage: storage,
    whitelist: [
        'goToLobby'
    ]
}

export default persistReducer(persistConfig, userHomepageReducer)
