import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import queryString from 'query-string';
import PlayerContainer from './PlayerContainer'
import Game from '../Game/Game';
import io from 'socket.io-client';
import { Button } from 'semantic-ui-react';
import {setRoom} from '../../actions/joinRoomActions'
import {addPlayer, removePlayer, removeAllPlayers, startGame, getPlayers} from '../../actions/gameActions'
import $ from 'jquery';

let socket;

export const MainLobby = (props) => {
    const dispatch = useDispatch();
    const roomID = useSelector(state => state.userHome.room_id)
    const ENDPOINT = 'localhost:3000'
    const createdLobby = useSelector(state => state.userHome.goToLobby)
    const gameStarted = useSelector(state => state.game.gameStarted)

    useEffect(() => {
        const {name, room} = queryString.parse(props.location.search)
        socket = io(ENDPOINT);
        if(createdLobby === true) {
            dispatch(setRoom(roomID))
        } 
        socket.emit('join',  {name, room}, (name) => {
        });
        return() => {
            socket.emit('disconnect');
            socket.off();
        }
    }, [ENDPOINT]);

    useEffect(() => {
        const player = queryString.parse(props.location.search)
        dispatch(addPlayer(player))
    }, [ENDPOINT]);

    useEffect(() => {
        const {name, room} = queryString.parse(props.location.search)
        dispatch(getPlayers(room))
    })


    const start = () => {
        dispatch(startGame())
    }

    if(gameStarted === false) {
        return (
           <Game/>
        )
    } else {
    return (
        <div>
            <h1>Main Lobby</h1>
            <div className='main-lobby-screen'>
                <button onClick={()=> dispatch(removeAllPlayers())}>Clear Lobby</button>
                <p> Users currently in the lobby Max 8</p>

                <Button onClick={() => start()}>Start Game</Button>
            </div>
        </div>
    )}
}

export default MainLobby
