import {FETCH_COLLECTION, GET_PLAYERS, ADD_PLAYER} from './types'



export const getQuestions = () => dispatch => {
    fetch('http://localhost:3000/collectioncards')
    .then(res => res.json())
    .then((collection) => {
        var title = collection[0].name
        var questions = [];

        collection.map((index) => (
            questions.push(index.prompt)
        ))

        dispatch({
            type: FETCH_COLLECTION,
            title,
            questions
        })
    } 
    )
}
export const addPlayer = (data) => async dispatch => {
        const player = data
        const url = `http://localhost:3000/players-in-room/`
        const settings = {
            method: "POST",
            headers: {
                'Content-Type':'application/json'
            },
            body: JSON.stringify(player)
        }
        var res = await fetch(url, settings)
        var newplayer = await res.json();
        dispatch({
            type: ADD_PLAYER,
            newplayer
        })
}

export const getPlayersInRoom = (room_id) => async dispatch => {
    const url = `http://localhost:3000/players-in-room/${room_id}`
    var res = await fetch(url)
    var players = await res.json();
    console.log(players)
    dispatch({
        type: GET_PLAYERS,
        players
    })
}
