import { GET_PLAYERS, ADD_PLAYER, SET_STAGE} from './types'




export const addPlayer = (player) => async dispatch => {
        const url = 'http://localhost:3000/players-in-game/'
        const settings = {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify(player)
        }
        var res = await fetch(url, settings)
        var newPlayer = await res.json();
        dispatch({
            type: ADD_PLAYER,
            payload: newPlayer.name

        })

}

export const getPlayersInRoom = (room_id) => async dispatch => {
    const url = `http://localhost:3000/players-in-room/${room_id}`
    var res = await fetch(url)
    var players = await res.json();
    // var playerNames = [];
    // players.map((player) => (
    //     playerNames.push(player.name)
    // ))
    dispatch({
        type: GET_PLAYERS,
        players
    })
}

