import { FETCH_COLLECTION, SET_MESSAGES } from "../actions/types";

const initialState = {
    deckName: '',
    prompts: {},
    message: '',
    messages: [],
    test: null
}

export default function gameReducer (state = initialState, action) {
    switch(action.type) {
        case FETCH_COLLECTION: 
          return {
            ...state,
            prompts: action.prompts,
            deckName: action.deckName

          }
        case SET_MESSAGES:
          return {
            ...state,
            messages: [...state.messages, action.payload],
            test: action.payload
          }
        default:
          return {
            ...state
          }
    }
}
