import { SIGN_UP, FETCH_USERS } from '../actions/types'

const intialState = {
    users: []
}

export default function signupReducer(state = intialState, action) {
    switch(action.type) {
        case FETCH_USERS:
            return {
                ...state,
                users: action.payload
            }
        case SIGN_UP:
            return {
            ...state,
            user: action.payload
            }
        default:
            return {
                ...state
            }
            // making my fetch request and adding them to all users
    }
}
