import {FETCH_COLLECTION, ADD_PLAYER, REMOVE_PLAYER} from './types'



export const getQuestions = () => dispatch => {
    fetch('http://localhost:3000/collectioncards')
    .then(res => res.json())
    .then((collection) => {
        var title = collection[0].name
        var questions = [];

        collection.map((index) => {
            questions.push(index.prompt)
        })

        dispatch({
            type: FETCH_COLLECTION,
            title,
            questions
        })
    } 
    )
}

export const getPlayers = (name) => dispatch => {
    dispatch({
        type: ADD_PLAYER,
        payload: name
    })
}

export const removePlayer = (name) => dispatch => {
    dispatch({
        type: REMOVE_PLAYER,
        payload: name
    })
}
