import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux';
import queryString from 'query-string';
import io from 'socket.io-client';
import {setName, setRoom} from '../../actions/joinRoomActions'


let socket;


export const MainLobby = (props) => {
    const dispatch = useDispatch();
    const roomId = useSelector(state => state.joinRoom.room_id)
    const getName = useSelector(state => state.joinRoom.name)
    const ENDPOINT = 'localhost:3000'
    useEffect(() => {
        const {name, room} = queryString.parse(props.location.search)
        dispatch(setName(name))
        dispatch(setRoom(room))
    }, [roomId])

    useEffect((roomId, getName) => {
        socket = io(ENDPOINT);

        socket.emit('join', { getName, roomId });
        console.log(roomId, getName)
        return() => {
            socket.emit('disconnect');
            console.log('unmount')
        }
    }, [])
    console.log(roomId, getName)
    return (
        <h1>Main Lobby</h1>
    )
}

export default MainLobby
