import React, {useEffect} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import { Button, Form} from 'semantic-ui-react'
import { fetchUsers, createUser } from '../../actions/userActions'


export const SignupForm = (props) => {
    const allUsers = useSelector(state => state.users)
    useEffect((props) => {
        console.log("This is like componentDidMount")
    }, [])

    async function handleSubmit(e) {
        e.preventDefault()
        const new_user = {
            username: e.target.name.value,
            email: e.target.email.value,
            password: e.target.password.value
        }
    }
//  if (this.state.redirect) {
// return <Redirect push to={`/student/${this.state.userID}/home`}></Redirect>
    return(
        <div>
        <h1>This is the Signup Form</h1>
        <Form onSubmit={handleSubmit}>
        <Form.Field required>
            <label>Name: </label>
            <input placeholder='Name' name='name'/>
        </Form.Field>
        <Form.Field required>
            <label>Email: </label>
            <input placeholder='email' name='email'/>
        </Form.Field>
        <Form.Field required>
            <label>Password</label>
            <input type='password' name='password'/>
        </Form.Field>
        <Form.Field required>
            <label>Confirm Password</label>
            <input type='password' name='password_confirmation'/>
        </Form.Field>
        <Button type='submit' className='signupButton'>Submit</Button>
        </Form>
        </div>
    )
}

export default SignupForm
